mapper.select:
		select `cid`,`title`,`created`,`modified`,`content`,`tags`,`categories`,`comments_num`,`status`,`likes_count`,`author`,`gtm_create`,`gtm_modified`
		from blog_content

		  		   and cid = #{cid} 
		  		   and title = #{title} 
		  		   and created = #{created} 
		  		   and modified = #{modified} 
			       and type = #{type} 
			       and content = #{content} 
		  		   and tags = #{tags} 
		  		   and categories = #{categories} 
		  		   and comments_num = #{comments_num} 
		  		   and status = #{status} 
		  		   and author = #{author} 
			       and likes_count = #{likes_count} 
		  		   and gtm_create = #{gtm_create} 
		  		   and gtm_modified = #{gtm_modified} 



                order by ${sort} ${order}


                order by cid desc



			limit ${offset}, ${limit}

select count(*) from blog_content

  		   and cid = #{cid} 
  		   and title = #{title} 
  		   and created = #{created} 
  		   and modified = #{modified} 
		   and type = #{type} 
		   and content = #{content} 
  		   and tags = #{tags} 
  		   and categories = #{categories} 
  		   and comments_num = #{comments_num} 
  		   and status = #{status} 
  		   and author = #{author} 
	       and likes_count = #{likes_count} 
  		   and gtm_create = #{gtm_create} 
  		   and gtm_modified = #{gtm_modified}

mapper.update:
update blog_content
`title` = #{title}, `created` = #{created}, `modified` = #{modified}, `content` = #{content}, `tags` = #{tags}, `categories` = #{categories}, `comments_num` = #{comments_num}, `status` = #{status}, `author` = #{author}, and likes_count = #{likes_count} `gtm_create` = #{gtm_create}, `gtm_modified` = #{gtm_modified}
where cid = #{cid}
