mapper.insert:
insert into user (id, username, password , email, phone_no, create_time, create_no, update_time, update_no
  )
values (#{id}, #{username}, #{password}, #{email},
       #{phoneNo}, #{createTime},#{createNo},#{updateTime},#{updateNo}
  )

mapper.select:
select id, username, password , email, phone_no, create_time, create_no, update_time, update_no
from user
where id = #{id,jdbcType=BIGINT}

select id, username, password , email, phone_no, create_time, create_no, update_time, update_no
from user
where username = #{username,jdbcType=VARCHAR}
